import React, { useState } from 'react';
import Board from './Board';
import { calculateWinner } from './CalculateWinner';

function UltimateTicTacToe() {
  const [boards, setBoards] = useState(Array(9).fill(Array(9).fill(null)));
  const [activeBoard, setActiveBoard] = useState(null);
  const [isX, setIsX] = useState(true);

  const handleBoardClick = (boardIndex, squareIndex) => {
    if (activeBoard !== null && activeBoard !== boardIndex) {
      return;
    }

    const updatedBoards = [...boards];
    const updatedBoard = [...updatedBoards[boardIndex]];

    if (updatedBoard[squareIndex] || calculateWinner(updatedBoard)) {
      return;
    }

    updatedBoard[squareIndex] = isX ? 'X' : 'O';
    updatedBoards[boardIndex] = updatedBoard;

    setBoards(updatedBoards);
    setActiveBoard(squareIndex);
    setIsX(!isX);
  };

  const restartGame = () => {
    setBoards(Array(9).fill(Array(9).fill(null)));
    setActiveBoard(null);
    setIsX(true);
  };

  const winner = calculateWinner(boards[activeBoard]);

  return (
    <div className="ultimate-tic-tac-toe">
      {winner && <div className="winner">Winner: {winner}</div>}
      <div className="board-container">
        {boards.map((board, index) => (
          <Board
            key={index}
            squares={board}
            active={activeBoard === index}
            onClick={(squareIndex) => handleBoardClick(index, squareIndex)}
          />
        ))}
      </div>
      <button className="restart-button" onClick={restartGame}>
        Restart
      </button>
    </div>
  );
}

export default UltimateTicTacToe;
